"use client"

import React from "react"
import { Disclosure, Transition } from "@headlessui/react"
import { cn, ComponentWithAnatomy, createPolymorphicComponent, defineStyleAnatomy } from "../core"
import { cva } from "class-variance-authority"

/* -------------------------------------------------------------------------------------------------
 * Anatomy
 * -----------------------------------------------------------------------------------------------*/

export const AccordionAnatomy = defineStyleAnatomy({
    container: cva([
        "UI-Accordion__container",
        "space-y-2"
    ]),
    trigger: cva([
        "UI-Accordion__trigger",
        "bg-gray-50 dark:bg-gray-800 hover:bg-gray-100 dark:hover:bg-gray-700",
        "flex w-full justify-between rounded-lg px-4 py-3 text-left font-medium transition",
        "focus:outline-none focus-visible:ring-2 focus-visible:ring-[--ring] ring-offset-1",
    ]),
    panel: cva([
        "UI-Accordion__panel",
        "py-2"
    ]),
    item: cva(["UI-Accordion__item"]),
})

/* -------------------------------------------------------------------------------------------------
 * Accordion
 * -----------------------------------------------------------------------------------------------*/

export interface AccordionProps extends React.ComponentPropsWithoutRef<"div">, ComponentWithAnatomy<typeof AccordionAnatomy> {
}

const _Accordion = (props: AccordionProps) => {

    const {
        children,
        triggerClassName,
        containerClassName,
        panelClassName,
        itemClassName,
        className,
        ...rest
    } = props

    const itemsWithProps = React.Children.map(children, (child) => {
        // Checking isValidElement is the safe way and avoids a typescript error too.
        if (React.isValidElement(child)) {
            return React.cloneElement(child, { triggerClassName, panelClassName, itemClassName } as any)
        }
        return child
    })

    return (
        <div className={cn(AccordionAnatomy.container(), containerClassName, className)}>
            {itemsWithProps}
        </div>
    )

}

/* -------------------------------------------------------------------------------------------------
 * Accordion.Item
 * -----------------------------------------------------------------------------------------------*/

interface AccordionItemProps extends React.ComponentPropsWithoutRef<"div">,
    Omit<ComponentWithAnatomy<typeof AccordionAnatomy>, "containerClassName"> {
    title: string,
}

export const AccordionItem: React.FC<AccordionItemProps> = (
    {
        children,
        title,
        triggerClassName,
        panelClassName,
        itemClassName,
        className,
        ...rest
    }) => {

    return (
        <Disclosure>
            {({ open }) => (
                <div className={cn(AccordionAnatomy.item(), itemClassName, className)} {...rest}>
                    <Disclosure.Button className={cn(AccordionAnatomy.trigger(), triggerClassName)}>
                        {title}
                        <svg
                            className={cn(
                                "ml-1.5 h-5 w-5 flex-shrink-0 transition duration-300",
                                {
                                    "-rotate-180": open,
                                },
                            )}
                            xmlns="http://www.w3.org/2000/svg"
                            fill="none"
                            viewBox="0 0 24 24"
                            stroke="currentColor"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth="2"
                                d="M19 9l-7 7-7-7"
                            />
                        </svg>
                    </Disclosure.Button>
                    <Transition
                        show={open}
                        enter="transition duration-300 ease-out"
                        enterFrom="transform h-0 opacity-0"
                        enterTo="transform h-full opacity-100"
                        leave="transition duration-75 ease-out"
                        leaveFrom="transform h-full opacity-100"
                        leaveTo="transform h-0 opacity-0"
                    >
                        <Disclosure.Panel static className={cn(AccordionAnatomy.panel(), panelClassName)}>
                            {children}
                        </Disclosure.Panel>
                    </Transition>
                </div>
            )}
        </Disclosure>
    )
}

AccordionItem.displayName = "AccordionItem"

/* -------------------------------------------------------------------------------------------------
 * Component
 * -----------------------------------------------------------------------------------------------*/

_Accordion.Item = AccordionItem

export const Accordion = createPolymorphicComponent<"div", AccordionProps, {
    Item: typeof AccordionItem
}>(_Accordion)

Accordion.displayName = "Accordion"

"use client"

import { LoaderOptions } from "@googlemaps/js-api-loader"
import _isEmpty from "lodash/isEmpty"
import React, { useId } from "react"
import { extractBasicFieldProps } from "../basic-field"
import { Combobox, ComboboxProps } from "../combobox"
import { useUILocaleConfig } from "../core"
import locales from "./locales.json"
import { GoogleMapsAutocompletionRequest, useGoogleMapsAutocomplete } from "./use-address-autocomplete"

/* -------------------------------------------------------------------------------------------------
 * AddressInput
 * -----------------------------------------------------------------------------------------------*/

export interface AddressInputProps extends Omit<ComboboxProps, "options" | "onInputChange" | "onChange"> {
    autocompletionRequest?: GoogleMapsAutocompletionRequest
    apiOptions?: Partial<LoaderOptions>
    allowedCountries?: string | string[] | null
    onChange?: (value: string | undefined) => void
    noOptionsMessage?: string
    placeholder?: string
    apiKey: string // Optionally, you could remove this parameter and get the key from environment variables
}

export const AddressInput = React.forwardRef<HTMLInputElement, AddressInputProps>((props, ref) => {

    const { locale: lng } = useUILocaleConfig()

    const [{
        children,
        className,
        autocompletionRequest,
        apiOptions,
        defaultValue,
        allowedCountries = null,
        onChange,
        apiKey,
        placeholder = locales["placeholder"][lng],
        noOptionsMessage = locales["no-address-found"][lng],
        ...rest
    }, basicFieldProps] = extractBasicFieldProps<AddressInputProps>(props, useId())

    const { suggestions, fetchSuggestions } = useGoogleMapsAutocomplete({
        apiKey: apiKey,
        minLengthAutocomplete: 0,
        withSessionToken: false,
        debounce: 300,
        autocompletionRequest: {
            componentRestrictions: { country: allowedCountries },
        },
    })

    return (
        <>
            <Combobox
                returnValueOrLabel="label" // We only return the address' text format
                allowCustomValue={false}
                withFiltering={false} // We deactivate filtering because the options are automatically filtered by the API
                options={_isEmpty(suggestions) && defaultValue ? [{
                    value: defaultValue,
                    label: defaultValue
                }] : suggestions}
                onInputChange={fetchSuggestions}
                defaultValue={defaultValue}
                onChange={onChange}
                placeholder={placeholder}
                noOptionsMessage={noOptionsMessage}
                {...basicFieldProps}
                {...rest}
                ref={ref}
            />
        </>
    )

})

{
  "placeholder": {
    "fr": "Entrez l'addresse",
    "en": "Enter your address"
  },
  "no-address-found": {
    "fr": "Aucune addresse trouv√©e",
    "en": "No address found"
  }
}

"use client"

import { Loader, LoaderOptions } from "@googlemaps/js-api-loader"
import { useCallback, useEffect, useState } from "react"
import { useDebouncedCallback } from "use-debounce"

export type GoogleMapsAutocompletionRequest = Omit<google.maps.places.AutocompletionRequest, "input">

export interface GoogleMapsAutocompleteProps {
    apiKey: string,
    minLengthAutocomplete?: number,
    withSessionToken?: boolean
    debounce?: number
    autocompletionRequest?: GoogleMapsAutocompletionRequest
    loaderApiOptions?: Partial<LoaderOptions>
}

export const useGoogleMapsAutocomplete = ({
                                              apiKey,
                                              minLengthAutocomplete = 0,
                                              withSessionToken = false,
                                              debounce = 300,
                                              autocompletionRequest,
                                              loaderApiOptions = {},
                                          }:
                                              GoogleMapsAutocompleteProps) => {

    const [autocompleteService, setAutocompleteService] = useState<google.maps.places.AutocompleteService | undefined>(undefined)
    const [sessionToken, setSessionToken] = useState<google.maps.places.AutocompleteSessionToken | undefined>(undefined)

    const initializeService = useCallback(() => {
        if (!window.google) throw new Error("[AddressInput]: Google script not loaded")
        if (!window.google.maps) throw new Error("[AddressInput]: Google maps script not loaded")
        if (!window.google.maps.places) throw new Error("[AddressInput]: Google maps places script not loaded")

        setAutocompleteService(new window.google.maps.places.AutocompleteService())
        setSessionToken(new google.maps.places.AutocompleteSessionToken())
    }, [window])

    /**
     * Initialize
     */
    useEffect(() => {
        const init = async () => {
            try {
                if (!window.google || !window.google.maps || !window.google.maps.places) {
                    await new Loader({ apiKey: apiKey, ...{ libraries: ["places"], ...loaderApiOptions } }).load()
                }
                initializeService()
            } catch (error) {
                console.log(error)
            }
        }

        if (apiKey) init()
        else initializeService()
    }, [])


    /**
     * Fetch suggestions
     */
    const [suggestions, setSuggestions] = useState<{ label: string, value: string }[]>([])

    const fetchSuggestions = useDebouncedCallback((value: string): void => {
        if (!autocompleteService) return setSuggestions([])
        if (value.length < minLengthAutocomplete) return setSuggestions([])

        const autocompletionReq: GoogleMapsAutocompletionRequest = { ...autocompletionRequest }

        autocompleteService.getPlacePredictions(
            requestBuilder(
                autocompletionReq,
                value,
                withSessionToken && sessionToken,
            ), (suggestions) => {
                setSuggestions((suggestions || []).map(suggestion => ({
                    label: suggestion.description,
                    value: suggestion.place_id
                })))
            },
        )
    }, debounce)

    return {
        suggestions,
        fetchSuggestions,
    }

}

const requestBuilder = (
    autocompletionRequest: GoogleMapsAutocompletionRequest,
    input: string,
    sessionToken?: google.maps.places.AutocompleteSessionToken,
): google.maps.places.AutocompletionRequest => {
    const { location, ...rest } = autocompletionRequest

    const res: google.maps.places.AutocompletionRequest = {
        input,
        ...rest,
    }

    if (sessionToken) {
        res.sessionToken = sessionToken
    }

    if (location) {
        res.location = new google.maps.LatLng(location)
    }

    return res
}

