/* -------------------------------------------------------------------------------------------------
 * Extract BasicFieldProps
 * -----------------------------------------------------------------------------------------------*/

export function extractBasicFieldProps<Props extends BasicFieldOptions>(props: Props, id: string) {
    const {
        name,
        label,
        labelProps,
        help,
        error,
        isRequired,
        isDisabled = false,
        isReadOnly = false,
        fieldDetailsClassName,
        fieldLabelClassName,
