                            <text
                                x="50%"
                                y="50%"
                                textAnchor="middle"
                                dominantBaseline="middle"
                                fill={`var(--muted)`}
                                className={"font-semibold"}
                            >
                                {parsedLabelInput}
                            </text>
                        ) : null}
                        <Pie
                            data={parseChartData(data, colors)}
                            cx="50%"
                            cy="50%"
                            startAngle={90}
                            endAngle={-270}
                            innerRadius={isDonut ? "75%" : "0%"}
                            outerRadius="100%"
                            paddingAngle={0}
                            dataKey={category}
                            nameKey={index}
                            isAnimationActive={showAnimation}
                        />
                        {showTooltip ? (
                            <Tooltip
                                wrapperStyle={{ outline: "none" }}
                                content={({ active, payload }) => (
                                    <DonutChartTooltip
                                        active={active}
                                        payload={payload}
                                        valueFormatter={valueFormatter}
                                    />
                                )}
                            />
                        ) : null}
                    </ReChartsDonutChart>
                ) : (
                    <div>...</div>
                )}
            </ResponsiveContainer>
        </div>
    )
})

