export interface LineChartProps extends React.ComponentPropsWithRef<"div">,
    ComponentWithAnatomy<typeof LineChartAnatomy>,
    BaseChartProps {
    curveType?: ChartCurveType
    connectNulls?: boolean
}

export const LineChart: React.FC<LineChartProps> = React.forwardRef<HTMLDivElement, LineChartProps>((props, ref) => {

    const {
        rootClassName,
        className,
        curveType = "linear",
        connectNulls = false,
        /**/
        data = [],
        categories = [],
        index,
        colors = ColorPalette,
        valueFormatter = defaultValueFormatter,
        startEndOnly = false,
        showXAxis = true,
        showYAxis = true,
        yAxisWidth = 56,
        showAnimation = true,
        showTooltip = true,
        showLegend = true,
        showGridLines = true,
        showGradient = true,
        autoMinValue = false,
        minValue,
        maxValue,
        allowDecimals = true,
        noDataText,
        ...rest
    } = props

    const [legendHeight, setLegendHeight] = useState(60)

    const categoryColors = constructCategoryColors(categories, colors)
    const yAxisDomain = getYAxisDomain(autoMinValue, minValue, maxValue)

    return (
        <div
            className={cn(LineChartAnatomy.root(), rootClassName, className)}
            {...rest}
            ref={ref}
        >
            <ResponsiveContainer width={"100%"} height={"100%"}>
                {data?.length ? (
                    <ReChartsLineChart data={data}>
                        {showGridLines ? (
                            <CartesianGrid strokeDasharray="3 3" horizontal={true} vertical={false}/>
                        ) : null}
                        <XAxis
                            hide={!showXAxis}
                            dataKey={index}
                            tick={{ transform: "translate(0, 8)" }}
                            ticks={startEndOnly ? [data[0][index], data[data.length - 1][index]] : undefined}
                            style={{
                                fontSize: ".75rem",
                                fontFamily: "Inter; Helvetica",
                                color: "red",
                            }}
                            interval="preserveStartEnd"
                            axisLine={false}
                            tickLine={false}
                            padding={{ left: 10, right: 10 }}
                            minTickGap={5}
                        />
                        <YAxis
                            width={yAxisWidth}
                            hide={!showYAxis}
                            axisLine={false}
                            tickLine={false}
                            type="number"
                            domain={yAxisDomain as AxisDomain}
                            tick={{ transform: "translate(-3, 0)" }}
                            style={{
                                fontSize: ".8rem",
                                fontFamily: "Inter; Helvetica",
                            }}
                            tickFormatter={valueFormatter}
                            allowDecimals={allowDecimals}
                        />
                        {showTooltip ? (
                            <Tooltip
                                wrapperStyle={{ outline: "none" }}
                                isAnimationActive={false}
                                cursor={{ stroke: "#ddd", strokeWidth: 2 }}
                                position={{ y: 0 }}
                                content={({ active, payload, label }) => (
                                    <ChartTooltip
                                        active={active}
                                        payload={payload}
                                        label={label}
                                        valueFormatter={valueFormatter}
                                        categoryColors={categoryColors}
                                    />
                                )}
                            />
                        ) : null}

                        {categories.map((category) => (
                            <Line
                                key={category}
                                name={category}
                                type={curveType}
                                dataKey={category}
                                stroke={`var(--${categoryColors.get(category)})`}
                                strokeWidth={2}
                                dot={false}
                                isAnimationActive={showAnimation}
                                connectNulls={connectNulls}
                            />
                        ))}

                        {showLegend ? (
                            <Legend
                                verticalAlign="bottom"
                                height={legendHeight}
                                content={({ payload }) => ChartLegend({ payload }, categoryColors, setLegendHeight)}
                            />
                        ) : null}

                    </ReChartsLineChart>
                ) : (
                    <div>...</div>
                )}
            </ResponsiveContainer>
        </div>
    )

})

LineChart.displayName = "LineChart"

export type ChartValueFormatter = {
    (value: number): string
}

export type ChartCurveType = "linear" | "natural" | "step"

export type ChartHorizontalPosition = "left" | "right"

export type ChartVerticalPosition = "top" | "bottom"

export interface BaseChartProps extends React.HTMLAttributes<HTMLDivElement> {
    data: any[] | null | undefined
    categories: string[]
    index: string
    // Choose the color for each category
    colors?: string[]
    // Change the formatting
    valueFormatter?: ChartValueFormatter
    // Show only the first and last elements in the x-axis. Great for smaller charts or sparklines.
    startEndOnly?: boolean
    showXAxis?: boolean
    showYAxis?: boolean
    yAxisWidth?: number
    // Sets an animation to the chart when it is loaded.
    showAnimation?: boolean
    showTooltip?: boolean
    showGradient?: boolean
    showLegend?: boolean
    showGridLines?: boolean
    // Adjusts the minimum value in relation to the magnitude of the data.
    autoMinValue?: boolean
    // Sets the minimum value of the shown chart data.
    minValue?: number
    // Sets the maximum value of the shown chart data.
    maxValue?: number
    // Controls if the ticks of a numeric axis are displayed as decimals or not.
    allowDecimals?: boolean
    // The displayed text when the data is empty.
    noDataText?: string
}

import { ChartValueFormatter } from "../charts"
import { UIColor } from "../core"

/* -------------------------------------------------------------------------------------------------
 * Chart Utils
 * -----------------------------------------------------------------------------------------------*/

/**
 * @internal
 */
export const constructCategoryColors = (
    categories: string[],
    colors: UIColor[],
): Map<string, UIColor> => {
    const categoryColors = new Map<string, UIColor>()
    categories.forEach((category, idx) => {
        categoryColors.set(category, colors[idx] ?? "gray")
    })
    return categoryColors
}

/**
 * @internal
 */
export const getYAxisDomain = (
    autoMinValue: boolean,
    minValue: number | undefined,
    maxValue: number | undefined,
) => {
    const minDomain = autoMinValue ? "auto" : minValue ?? 0
    const maxDomain = maxValue ?? "auto"
    return [minDomain, maxDomain]
}

export const defaultValueFormatter: ChartValueFormatter = (value: number) => value.toString()

/* -------------------------------------------------------------------------------------------------
 * DonutChart Utils
 * -----------------------------------------------------------------------------------------------*/

export const parseChartData = (data: any[], colors: UIColor[]) =>
    data.map((dataPoint: any, idx: number) => {
        const baseColor = idx < colors.length ? colors[idx] : "brand"
        return {
            ...dataPoint,
            // explicitly adding color key if not present for tooltip coloring
            color: baseColor,
            fill: `var(--${baseColor})`, // Color
        }
    })

const sumNumericArray = (arr: number[]) =>
    arr.reduce((prefixSum, num) => prefixSum + num, 0)

const calculateDefaultLabel = (data: any[], category: string) =>
    sumNumericArray(data.map((dataPoint) => dataPoint[category]))

export const parseChartLabelInput = (
    labelInput: string | undefined,
    valueFormatter: ChartValueFormatter,
    data: any[],
    category: string,
) => (labelInput ? labelInput : valueFormatter(calculateDefaultLabel(data, category)))

"use client"

import { cn } from "../core"
import React, { createContext, useContext, useId, useLayoutEffect, useState } from "react"
import { BasicField, BasicFieldOptions, extractBasicFieldProps } from "../basic-field"
import { Checkbox, CheckboxProps } from "."


/* -------------------------------------------------------------------------------------------------
 * Provider
 * -----------------------------------------------------------------------------------------------*/

interface CheckboxGroupContextValue {
    group_size: CheckboxProps["size"]
}

const _CheckboxGroupContext = createContext<CheckboxGroupContextValue | null>(null)
export const CheckboxGroupProvider = _CheckboxGroupContext.Provider
export const useCheckboxGroupContext = () => useContext(_CheckboxGroupContext)

/* -------------------------------------------------------------------------------------------------
 * CheckboxGroup
 * -----------------------------------------------------------------------------------------------*/

export interface CheckboxGroupProps extends BasicFieldOptions {
    value?: string[]
    defaultValue?: string[]
    onChange?: (value: string[]) => void
    size?: CheckboxProps["size"]
    stackClassName?: string
    checkboxContainerClassName?: string
    checkboxLabelClassName?: string
    checkboxControlClassName?: string
    checkboxIconClassName?: string
    options: { value: string, label?: React.ReactNode }[]
}

export const CheckboxGroup = React.forwardRef<HTMLDivElement, CheckboxGroupProps>((props, ref) => {

    const [{
        value,
        defaultValue = [],
        onChange,
        stackClassName,
        checkboxLabelClassName,
        checkboxControlClassName,
        checkboxContainerClassName,
        checkboxIconClassName,
        options,
        size = undefined,
    }, basicFieldProps] = extractBasicFieldProps<CheckboxGroupProps>(props, useId())

    // Keep track of selected values
    const [selectedValues, setSelectedValues] = useState<string[]>(value ?? defaultValue)

    // Control the state
    useLayoutEffect(() => {
        if (value) {
            setSelectedValues(value)
        }
    }, [value])


    return (
        <>
            <CheckboxGroupProvider value={{
                group_size: size
            }}>
                <BasicField
                    {...basicFieldProps}
                    ref={ref}
                >
                    <div className={cn("space-y-1", stackClassName)}>
                        {options.map((opt) => (
                            <Checkbox
                                key={opt.value}
                                label={opt.label}
                                value={opt.value}
                                checked={selectedValues.includes(opt.value)}
                                onChange={checked => {
                                    setSelectedValues(p => {
                                        let newArr = [...p]
                                        if (checked === true) {
                                            if (p.indexOf(opt.value) === -1) newArr.push(opt.value)
                                        } else if (checked === false) {
                                            newArr = newArr.filter(v => v !== opt.value)
                                        }
                                        if (onChange) {
                                            onChange(newArr)
                                        }
                                        return newArr
                                    })
                                }}
                                error={basicFieldProps.error}
                                noErrorMessage
                                labelClassName={checkboxLabelClassName}
                                controlClassName={checkboxControlClassName}
                                containerClassName={checkboxContainerClassName}
                                iconClassName={checkboxIconClassName}
                                isDisabled={basicFieldProps.isDisabled}
                                isReadOnly={basicFieldProps.isReadOnly}
                                tabIndex={0}
                            />
                        ))}
                    </div>
                </BasicField>
            </CheckboxGroupProvider>
        </>
    )

})

CheckboxGroup.displayName = "CheckboxGroup"

