import _ from "lodash"
import { DataGridEditingValueUpdater } from "./datagrid-cell-input-field"
import { AnyZodObject, ZodIssue } from "zod"
import { useToast } from "../toast"

/**
 * DataGrid Props
 */
export type DataGridRowEditedEvent<T extends Record<string, any>> = {
    row: Row<T>
    originalData: T
    data: T
}

export type DataGridOnRowEdit<T extends Record<string, any>> = (event: DataGridRowEditedEvent<T>) => void

//----

export type DataGridRowValidationError<T extends Record<string, any>> = {
    row: Row<T>
    originalData: T
    data: T
    errors: ZodIssue[]
}

export type DataGridOnRowValidationError<T extends Record<string, any>> = (event: DataGridRowValidationError<T>) => void

//----

export type DataGridValidationRowErrors = Array<{ rowId: string, key: string, message: string }>

/**
 * Hook props
 */
type Props<T extends Record<string, any>> = {
    data: T[]
    table: Table<T>
    rows: Row<T>[]
    onRowEdit?: DataGridOnRowEdit<T>
    isDataMutating: boolean | undefined
    enableOptimisticUpdates: boolean
    onDataChange: React.Dispatch<React.SetStateAction<T[]>>
    optimisticUpdatePrimaryKey: string | undefined
    manualPagination: boolean
    schema: AnyZodObject | undefined
    onRowValidationError: DataGridOnRowValidationError<T> | undefined
}

export function useDataGridEditing<T extends Record<string, any>>(props: Props<T>) {

    const {
        data,
        table,
        rows,
        onRowEdit,
        isDataMutating,
        onDataChange,
        enableOptimisticUpdates,
        optimisticUpdatePrimaryKey,
        manualPagination,
        schema,
        onRowValidationError,
    } = props

    const toast = useToast()

    const leafColumns = table.getAllLeafColumns()
    // Keep track of the state of each editable cell
    const [editableCellStates, setEditableCellStates] = useState<{
        id: string,
        colId: string,
        rowId: string,
        isEditing: boolean
    }[]>([])

    // Track updated value
    const [activeValue, setActiveValue] = useState<unknown>(undefined)
    // Track current row data being updated
    const [rowData, setRowData] = useState<T | undefined>(undefined)
    // Track current row being updated
    const [row, setRow] = useState<Row<T> | undefined>(undefined)

    const [rowErrors, setRowErrors] = useState<DataGridValidationRowErrors>([])

    // Keep track of editable columns (columns defined with the `withEditing` helper)
    const editableColumns = useMemo(() => {
        return leafColumns.filter(n => n.getIsVisible() && !!(n.columnDef.meta as any)?.editingMeta)
    }, [leafColumns])

    useEffect(() => {
        if (manualPagination) {
            setActiveValue(undefined)
            setRowData(undefined)
            setRow(undefined)
            setEditableCellStates([])
        }
    }, [table.getState().pagination.pageIndex, table.getState().pagination.pageSize])

    // Keep track of editable cells (cells whose columns are editable)
    const editableCells = useMemo(() => {
        if (rows.length > 0) {
            return _.flatten(rows.map(row => row.getVisibleCells().filter(cell => !!editableColumns.find(col => col.id === cell.column.id)?.id)))
        }
        return []
    }, [rows])

    // Set/update editable cells
    useLayoutEffect(() => {
        // Control the states of individual cells that can be edited
        if (editableCells.length > 0) {
            editableCells.map(cell => {
                setEditableCellStates(prev => [...prev, {
                    id: cell.id,
                    colId: cell.column.id,
                    rowId: cell.row.id,
                    isEditing: false
                }])
            })
        }
    }, [editableCells])

    /**/
    const handleStartEditing = useCallback((cellId: string) => {
        // Manage editing state of cells
        setEditableCellStates(prev => {
            const others = prev.filter(prevCell => prevCell.id !== cellId)
            const cell = prev.find(prevCell => prevCell.id === cellId)

            if (cell && prev.every(prevCell => !prevCell.isEditing)) { // (Event 1) When we select a cell and nothing else is being edited
                return [...others, { ...cell, id: cellId, isEditing: true }]

            } else if (cell && prev.some(prevCell => prevCell.isEditing)) { // (Event 2) When another cell is being edited
                const otherCellBeingEdited = prev.find(prevCell => prevCell.isEditing) // Find the cell being edited

                if (otherCellBeingEdited?.rowId === cell?.rowId) { // Only allow cells on the same row to be edited
                    return [...others, { ...cell, id: cellId, isEditing: true }]
                }
            }
            return prev
        })
    }, [])

    /**/
    const getIsCellActivelyEditing = useCallback((cellId: string) => {
        return editableCellStates.some(cell => cell.id === cellId && cell.isEditing)
    }, [editableCellStates])
    /**/
    const getIsCellEditable = useCallback((cellId: string) => {
        return !!editableCellStates.find(cell => cell.id === cellId)
    }, [editableCellStates])
    /**/
    const getIsCurrentlyEditing = useCallback(() => {
        return editableCellStates.some(cell => cell.isEditing)
    }, [editableCellStates])
    /**/
    const getFirstCellBeingEdited = useCallback(() => {
        return editableCellStates.find(cell => cell.isEditing)
    }, [editableCellStates])
    /**/
    const handleStopEditing = useCallback(() => {
        setEditableCellStates(prev => {
            return prev.map(n => ({ ...n, isEditing: false }))
        })
    }, [])

    const mutationRef = React.useRef<boolean>(false)

    /**
     * When `isDataMutating` is provided to watch mutations,
     * Wait for it to be `false` to cancel editing
     */
    useEffect(() => {
        if (isDataMutating !== undefined && !isDataMutating && mutationRef.current) {
            handleStopEditing()
            mutationRef.current = false
        }
    }, [isDataMutating])

    /**
     * When `isDataMutating` is not provided, immediately cancel editing
     */
    useEffect(() => {
        if (isDataMutating === undefined) {
            handleStopEditing()
        }
    }, [mutationRef.current])

    const saveEdit = useCallback((transformedData?: T) => {
        if (!row || !rowData) return

        startTransition(() => {
            // Compare data
            if (!_.isEqual(rowData, row.original)) {
                // Return new data
                onRowEdit && onRowEdit({
                    originalData: row.original,
                    data: transformedData || rowData,
                    row: row,
                })

                // Optimistic update
                if (enableOptimisticUpdates && optimisticUpdatePrimaryKey) {
                    let clone = structuredClone(data)
                    const index = clone.findIndex(p => {
                        if (!p[optimisticUpdatePrimaryKey] || !rowData[optimisticUpdatePrimaryKey]) return false
                        return p[optimisticUpdatePrimaryKey] === rowData[optimisticUpdatePrimaryKey]
                    })
                    if (clone[index] && index > -1) {
                        clone[index] = rowData
                        onDataChange(clone) // Emit optimistic update
                    } else {
                        console.error("[DataGrid] Could not perform optimistic update. Make sure `optimisticUpdatePrimaryKey` is a valid property.")
                    }

                } else if (enableOptimisticUpdates) {
                    console.error("[DataGrid] Could not perform optimistic update. Make sure `optimisticUpdatePrimaryKey` is defined.")
                }

                // Immediately stop edit if optimistic updates are enabled
                if (enableOptimisticUpdates) {
                    handleStopEditing()
                } else {
                    // Else, we wait for `isDataMutating` to be false
                    mutationRef.current = true
                }
            } else {
                handleStopEditing()
            }

        })
    }, [row, rowData])

    const handleOnSave = useCallback(async () => {
        if (!row || !rowData) return
        setRowErrors([])

        // Safely parse the schema object when a `validationSchema` is provided
        if (schema) {
            try {
                const parsed = await schema.safeParseAsync(rowData)
                if (parsed.success) {
                    let finalData = structuredClone(rowData)
                    Object.keys(parsed.data).map(key => {
                        // @ts-ignore
                        finalData[key] = parsed.data[key]
                    })
                    saveEdit(finalData)
                } else {

                    parsed.error.errors.map(error => {
                        toast.error(error.message ?? "Error")
                        setRowErrors(prev => [
                            ...prev,
                            { rowId: row.id, key: String(error.path[0]), message: error.message }
                        ])
                    })

                    if (onRowValidationError) {
                        onRowValidationError({
                            data: rowData,
                            originalData: row.original,
                            row: row,
                            errors: parsed.error.errors
                        })
                    }
                }
            } catch (e) {
                console.error('[DataGrid] Could not perform validation')
            }
        } else {
            saveEdit()
        }

    }, [row, rowData])

    /**
     * This fires every time the user changes an input
     */
    const handleUpdateValue = useCallback<DataGridEditingValueUpdater<T>>((value, _row, cell, zodType) => {
        setActiveValue(value) // Set the updated value (could be anything)
        setRow(_row) // Set the row being updated
        setRowData(prev => ({
            // If we are updating a different row, reset the rowData, else keep the past updates
            ...((row?.id !== _row.id || !rowData) ? _row.original : rowData),
            [cell.column.id]: value,
        }))
    }, [row, rowData])


    return {
        handleStartEditing,
        getIsCellActivelyEditing,
        getIsCellEditable,
        getIsCurrentlyEditing,
        getFirstCellBeingEdited,
        handleStopEditing,
        handleOnSave,
        handleUpdateValue,
        rowErrors,
    }

}

import { Column, ColumnFiltersState, Table } from "@tanstack/react-table"
import React, { useMemo } from "react"
import { getColumnHelperMeta } from "./helpers"
import addDays from "date-fns/addDays"
import isSameDay from "date-fns/isSameDay"

interface DataGridFilteringHookProps<T> {
    table: Table<T>,
    columnFilters: ColumnFiltersState,
}

export function useDataGridFiltering<T>(props: DataGridFilteringHookProps<T>) {

    const {
        table,
        columnFilters,
    } = props

    /**
     * Item filtering
     */
    const [filterableColumns, filteredColumns] = useMemo(() => {
        return [
            table.getAllLeafColumns().filter(col => col.getCanFilter() && !!getColumnHelperMeta(col, "filteringMeta")),
            table.getAllLeafColumns().filter(col => columnFilters.map(filter => filter.id).includes(col.id)),
        ]
    }, [table.getAllLeafColumns(), columnFilters])
    const unselectedFilterableColumns = filterableColumns.filter(n => !columnFilters.map(c => c.id).includes(n.id))

    // Get the default value for a filter when the user selects it
    const getFilterDefaultValue = React.useCallback((col: Column<any>) => {
        // Since the column is filterable, get options
        const options = getColumnHelperMeta(col, "filteringMeta")
        if (options) {
            if (options.type === "select" || options.type === "radio") {
                return options.options?.[0]?.value ?? ""
            } else if (options.type === "boolean") {
                return true
            } else if (options.type === "checkbox") {
                return options.options?.map(n => n.value) ?? []
            } else if (options.type === "date-range") {
                return { start: new Date(), end: addDays(new Date(), 7) }
            }
        }
        return null
    }, [])

    return {
        getFilterDefaultValue,
        unselectedFilterableColumns,
        filteredColumns,
        filterableColumns,
    }

}

export const dateRangeFilter = (row: any, columnId: string, filterValue: any) => {
    const value: Date = row.getValue(columnId)
    return (value >= filterValue.start && value <= filterValue.end) || isSameDay(value, filterValue.start) || isSameDay(value, filterValue.end)
}

import { useDataGridSize } from "./use-datagrid-size"
import React from "react"
import { Table } from "@tanstack/react-table"

interface DataGridResponsivenessHookProps<T extends Record<string, any>> {
    hideColumns: { below: number, hide: string[] }[] | undefined,
    table: Table<T>
}

export function useDataGridResponsiveness<T extends Record<string, any>>(props: DataGridResponsivenessHookProps<T>) {

    const {
        hideColumns = [],
        table,
    } = props

    const [tableRef, { width: tableWidth }] = useDataGridSize<HTMLDivElement>()

    React.useLayoutEffect(() => {
        hideColumns.map(({ below, hide }) => {
            table.getAllLeafColumns().map(column => {
                if (hide.includes(column.id)) {
                    if (tableWidth !== 0 && tableWidth < below) {
                        if (column.getIsVisible()) column.toggleVisibility(false)
                    } else {
                        if (!column.getIsVisible()) column.toggleVisibility(true)
                    }
                }
            })
        })
    }, [hideColumns, tableWidth])

    return {
        tableRef,
        tableWidth,
    }

}

import { startTransition, useEffect, useLayoutEffect, useMemo, useRef, useState } from "react"
import { Row, Table } from "@tanstack/react-table"
import _ from "lodash"

/**
 * DataGrid Prop
 */
export type DataGridOnRowSelect<T> = (event: DataGridRowSelectedEvent<T>) => void

/**
 * Hook props
 */
type Props<T> = {
    persistent: boolean
    onRowSelect?: DataGridOnRowSelect<T>
    table: Table<T>,
    data: T[] | null
    displayedRows: Row<T>[]
    rowSelectionPrimaryKey: string | undefined
    enabled: boolean
}

/**
 * Event
 */
export type DataGridRowSelectedEvent<T> = {
    data: T[]
}

export function useDataGridRowSelection<T extends Record<string, any>>(props: Props<T>) {

    const {
        table,
        data,
        onRowSelect,
        persistent,
        rowSelectionPrimaryKey: key,
        displayedRows,
        enabled,
    } = props

    const canSelect = useRef<boolean>(enabled)

    // Server mode
    const pageIndex = useMemo(() => table.getState().pagination.pageIndex, [table.getState().pagination.pageIndex])
    const pageSize = useMemo(() => table.getState().pagination.pageSize, [table.getState().pagination.pageSize])
    const globalFilter = useMemo(() => table.getState().globalFilter, [table.getState().globalFilter])
    const columnFilters = useMemo(() => table.getState().globalFilter, [table.getState().columnFilters])
    const sorting = useMemo(() => table.getState().sorting, [table.getState().sorting])
    // Server mode
    const displayedRowsRef = useRef<Row<T>[]>(displayedRows)
    // Server mode
    const previousSelectionEvent = useRef<DataGridRowSelectedEvent<T>>({ data: [] })
    // Server mode
    const [nonexistentSelectedRows, setNonexistentSelectedRows] = useState<{ id: string, row: Row<T> }[]>([])

    const rowSelection = useMemo(() => table.getState().rowSelection, [table.getState().rowSelection])
    const rows = useMemo(() => table.getRowModel().rows, [table.getRowModel().rows])

    // Warnings
    useEffect(() => {
        if (enabled && !key) {
            console.error("[DataGrid] You've enable row selection without providing a primary key. Make sure to define the `rowSelectionPrimaryKey` prop.")
            canSelect.current = false
        }
    }, [])

    const firstCheckRef = useRef<boolean>(false)
    useEffect(() => {
        if (enabled && key && !firstCheckRef.current && displayedRows.length > 0 && !displayedRows.some(row => !!row.original[key])) {
            console.error("[DataGrid] The key provided by `rowSelectionPrimaryKey` does not match any property in the data.")
            firstCheckRef.current = true
            canSelect.current = false
        }
    }, [displayedRows])

    /** Server-side row selection **/
    useLayoutEffect(() => {
        // When the table is paginated
        if (displayedRows.length > 0 && persistent && !!key && canSelect.current) {
            startTransition(() => {
                table.resetRowSelection()
                // Actualize nonexistent rows
                setNonexistentSelectedRows(prev => {
                    // Find the rows that were selected on the previous page
                    const rowIsSelected = (row: Row<T>) => Object.keys(rowSelection).map(v => parseInt(v)).includes(row.index)
                    const rowDoesntAlreadyExist = (row: Row<T>) => !prev.find(sr => sr.id === row.original[key])

                    const selectedRows = displayedRowsRef.current.filter(rowIsSelected).filter(rowDoesntAlreadyExist)

                    if (selectedRows.length > 0) {
                        return [...prev, ...selectedRows.map(row => ({ id: row.original[key], row: row }))]
                    }

                    return prev
                })
                displayedRowsRef.current = displayedRows // Actualize displayed row
            })

            displayedRows.map(displayedRow => {
                if (nonexistentSelectedRows.some(row => row.id === displayedRow.original[key])) {
                    // If the currently displayed row is in the nonexistent array but isn't selected, select it
                    if (displayedRow.getCanSelect() && !displayedRow.getIsSelected()) {
                        displayedRow.toggleSelected(true)
                        // Then remove it from nonexistent array
                        setNonexistentSelectedRows(prev => {
                            return [...prev.filter(row => row.id !== displayedRow.original[key])]
                        })
                    }
                }
            })
        }

    }, [pageIndex, pageSize, displayedRows, globalFilter, columnFilters, sorting])

    /** Client-side row selection **/
    useEffect(() => {
        if (!persistent && data && data?.length > 0 && canSelect.current) {
            const selectedIndices = Object.keys(rowSelection).map(v => parseInt(v))

            if (selectedIndices.length > 0) {

                onRowSelect && onRowSelect({
                    data: data.filter((v, i) => selectedIndices.includes(i)) ?? [],
                })

            }
        }
    }, [rowSelection, rows])

    useEffect(() => {
        /** Server-side row selection **/
        if (persistent && data && data?.length > 0 && canSelect.current && key) {
            const selectedIndices = Object.keys(rowSelection).map(v => parseInt(v))

            if ((+(Object.keys(rowSelection).length) + (nonexistentSelectedRows.length)) > 0) {

                startTransition(() => {
                    const result = {
                        data: [
                            ...data.filter((v, i) => selectedIndices.includes(i)),
                            ...nonexistentSelectedRows.map(nr => nr.row.original),
                        ],
                    }
                    // Compare current selection with previous
                    if (!isArrayEqual(result.data, previousSelectionEvent.current.data)) {
                        onRowSelect && onRowSelect(result)
                        previousSelectionEvent.current = result
                    }
                })

            }
        }
    }, [rowSelection])


    return {
        // On client-side row selection, the count is simply what is visibly selection. On server-side row selection, the count is what is visible+nonexistent rows
        selectedRowCount: persistent ? +(Object.keys(rowSelection).length) + (nonexistentSelectedRows.length) : Object.keys(rowSelection).length,
    }

}


const isArrayEqual = function (x: Array<Record<string, any>>, y: Array<Record<string, any>>) {
    return _(x).differenceWith(y, _.isEqual).isEmpty()
}

import { useCallback, useState } from "react"
import { useEventListener, useIsomorphicLayoutEffect } from "../core"

export function useDataGridSize<T extends HTMLElement = HTMLDivElement>(): [
    (node: T | null) => void,
    { width: number, height: number },
] {
    const [ref, setRef] = useState<T | null>(null)
    const [size, setSize] = useState<{ width: number, height: number }>({
        width: 0,
        height: 0,
    })

    const handleSize = useCallback(() => {
        setSize({
            width: ref?.offsetWidth || 0,
            height: ref?.offsetHeight || 0,
        })

    }, [ref?.offsetHeight, ref?.offsetWidth])

    useEventListener("resize", handleSize)

    useIsomorphicLayoutEffect(() => {
        handleSize()
    }, [ref?.offsetHeight, ref?.offsetWidth])

    return [setRef, size]
}

