 */

type DatePickerFieldProps = Omit<DatePickerProps, "value"> & { value?: Date }

/**
 * @zod z.date()
 * @example
 * <Field.DateRangePicker
 *    name="name"
 *    label="Appointment date"
 *    minValue={today(getLocalTimeZone())}
