    max?: number
}

const getType = (field: z.ZodTypeAny) => {
    switch (field._def.typeName) {
        case "ZodArray":
            return "array"
        case "ZodObject":
            return "object"
        case "ZodNumber":
            return "number"
        case "ZodDate":
            return "date"
        case "ZodString":
        default:
            return "text"
    }
}

const getArrayOption = (field: any, name: string) => {
    return field._def[name]?.value
}

/**
 * A helper function to render forms automatically based on a Zod schema
 *
 * @param schema The Yup schema
 * @returns {FieldProps[]}
 */
export const getFieldsFromSchema = (schema: z.ZodTypeAny): FieldValues[] => {
    const fields: FieldValues[] = []

    let schemaFields: Record<string, any> = {}
    if (schema._def.typeName === "ZodArray") {
        schemaFields = schema._def.type.shape
    } else if (schema._def.typeName === "ZodObject") {
        schemaFields = schema._def.shape()
    } else {
        return fields
    }

    for (const name in schemaFields) {
        const field = schemaFields[name]

        const options: Options = {}
        if (field._def.typeName === "ZodArray") {
            options.min = getArrayOption(field, "minLength")
            options.max = getArrayOption(field, "maxLength")
        }

        const meta = field.description && zodParseMeta(field.description)

        fields.push({
            name,
            label: meta?.label || field.description || name,
            type: meta?.type || getType(field),
            ...options,
        })
    }
    return fields
}


export const getNestedSchema = (schema: z.ZodTypeAny, path: string) => {
    return get(schema._def.shape(), path)
}

export const zodFieldResolver = <T extends z.ZodTypeAny>(schema: T) => {
    return {
        getFields() {
            return getFieldsFromSchema(schema)
        },
        getNestedFields(name: string) {
            return getFieldsFromSchema(getNestedSchema(schema, name))
        },
    }
}

export interface ZodMeta {
    label: string
    type?: string
}

export const zodMeta = (meta: ZodMeta) => {
    return JSON.stringify(meta)
}

export const zodParseMeta = (meta: string) => {
    try {
        return JSON.parse(meta)
    } catch (e) {
        return meta
    }
}

/**
 * @link https://github.com/colinhacks/zod/discussions/1953#discussioncomment-4811588
 * @param schema
 */
export function getZodDefaults<Schema extends z.AnyZodObject>(schema: Schema) {
    return Object.fromEntries(
        Object.entries(schema.shape).map(([key, value]) => {
            if (value instanceof z.ZodDefault) return [key, value._def.defaultValue()]
            return [key, undefined]
        }),
    )
}

/**
 * @param schema
 */
export function getZodDescriptions<Schema extends z.AnyZodObject>(schema: Schema) {
    return Object.fromEntries(
        Object.entries(schema.shape).map(([key, value]) => {
            return [key, (value as any)._def.description ?? undefined]
        }),
    )
}

/**
 * @example
 * const meta = useMemo(() => getZodParsedDescription<{ minValue: CalendarDate }>(schema, props.name), [])
 * @param schema
 * @param key
 */
export function getZodParsedDescription<T extends {
    [p: string]: any
}>(schema: z.AnyZodObject, key: string): T | undefined {
    const obj = getZodDescriptions(schema)
    const parsedDescription = _isString(obj[key]) ? JSON.parse(obj[key]) : undefined
    if (_isObject(parsedDescription)) {
        return parsedDescription as T
    }
    return undefined

}

"use client"

import React, { Fragment } from "react"
import { cn, ComponentWithAnatomy, defineStyleAnatomy } from "../core"
import { cva } from "class-variance-authority"
import { Disclosure } from "@headlessui/react"

/* -------------------------------------------------------------------------------------------------
 * Anatomy
 * -----------------------------------------------------------------------------------------------*/

export const VerticalNavAnatomy = defineStyleAnatomy({
    nav: cva([
        "UI-VerticalNav__nav",
        "block space-y-1"
    ]),
    item: cva([
        "UI-VerticalNav__tab",
        "group/vnav flex flex-none truncate items-center px-4 py-2 text-sm font-[600] rounded-[--radius] transition cursor-pointer",
        "hover:bg-[--highlight] hover:text-[--text-color]",
        "focus-visible:ring-2 ring-[--ring] outline-none",
        "text-[--muted]",
        "data-[selected=true]:text-[--brand]"
    ]),
    parentItem: cva([
        "UI-VerticalNav__parentItem",
        "cursor-pointer",
    ]),
    parentItemChevron: cva([
        "UI-VerticalNav__parentItemChevron",
        "w-5 h-5 transition-transform data-[open=true]:rotate-90",
    ]),
    icon: cva([
        "UI-VerticalNav__icon",
        "flex-shrink-0 -ml-1 mr-3 h-6 w-6",
        "text-[--muted]",
        "group-hover/vnav:text-[--text-color] data-[selected=true]:text-[--brand] data-[selected=true]:group-hover/vnav:text-[--brand]"
    ]),
    subList: cva([
        "UI-VerticalNav__subList",
        "pl-2",
    ]),
})

/* -------------------------------------------------------------------------------------------------
 * VerticalNav
 * -----------------------------------------------------------------------------------------------*/

export interface VerticalNavProps extends React.ComponentPropsWithRef<"div">, ComponentWithAnatomy<typeof VerticalNavAnatomy> {
    children?: React.ReactNode
    items: {
        name: string
        href?: string | null | undefined
        icon?: ((props: any) => JSX.Element) | null | undefined
        isCurrent?: boolean
        onClick?: React.MouseEventHandler<HTMLElement>
        addon?: React.ReactNode
        content?: React.ReactNode
    }[]
}

export const VerticalNav = React.forwardRef<HTMLDivElement, VerticalNavProps>((props, ref) => {

    const {
        children,
        navClassName,
        itemClassName,
        iconClassName,
        parentItemClassName,
        subListClassName,
        parentItemChevronClassName,
        className,
        items,
        ...rest
    } = props

    return (
        <nav
            ref={ref}
            className={cn(VerticalNavAnatomy.nav(), navClassName, className)}
            {...rest}
        >
            {items.map((item, idx) => !item.content ? (
                <a
                    key={item.name}
                    href={item.href ?? "#"}
                    className={cn(
                        VerticalNavAnatomy.item(),
                        itemClassName,
                    )}
                    aria-current={item.isCurrent ? "page" : undefined}
                    data-selected={item.isCurrent}
                    onClick={item.onClick}
                >
                    {item.icon && <item.icon
                        className={cn(
                            VerticalNavAnatomy.icon(),
                            iconClassName,
                        )}
                        aria-hidden="true"
                        data-selected={item.isCurrent}
                    />}
                    <span>{item.name}</span>
                    {item.addon}
                </a>
            ) : (
                <Disclosure as={Fragment} key={item.name}>
                    {({ open }) => (
                        <>
                            <Disclosure.Button
                                as="div"
                                key={item.name}
                                tabIndex={idx}
                                className={cn(
                                    VerticalNavAnatomy.item(),
                                    VerticalNavAnatomy.parentItem(),
                                    itemClassName,
                                    parentItemClassName,
                                )}
                                aria-current={item.isCurrent ? "page" : undefined}
                                data-selected={item.isCurrent}
                                onClick={item.onClick}
                            >
                                <div className="w-full flex items-center">
                                    {item.icon && <item.icon
                                        className={cn(
                                            VerticalNavAnatomy.icon(),
                                            iconClassName,
                                        )}
                                        aria-hidden="true"
                                        data-selected={item.isCurrent}
                                    />}
                                    <span>{item.name}</span>
                                </div>
                                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"
                                     fill="none" stroke="currentColor"
                                     strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"
                                     className={cn(VerticalNavAnatomy.parentItemChevron(), parentItemChevronClassName)}
                                     data-open={`${open}`}
                                >
                                    <polyline points="9 18 15 12 9 6"></polyline>
                                </svg>
                            </Disclosure.Button>
                            <Disclosure.Panel
                                className={cn(VerticalNavAnatomy.subList(), subListClassName)}
                            >
                                {item.content && item.content}
                            </Disclosure.Panel>
                        </>
                    )}
                </Disclosure>
            ))}
        </nav>
    )

})

VerticalNav.displayName = "VerticalNav"
