import { cn } from "@rahimstack/tailwind-utils"
import { cva, VariantProps } from "class-variance-authority"
import React from "react"
import { Button, ButtonProps } from "../button"
import { ComponentWithAnatomy, defineStyleAnatomy } from "../core"

export const IconButtonAnatomy = defineStyleAnatomy({
   iconButton: cva("UI-IconButton__icon-button p-0", {
      variants: {
         size: {
            xs: "text-xl h-6 w-6",
            sm: "text-xl h-8 w-8",
            md: "text-2xl h-10 w-10",
            lg: "text-3xl h-12 w-12",
            xl: "text-4xl h-14 w-14",
         },
      },
      defaultVariants: {
         size: "md",
      },
   }),
})


export interface IconButtonProps extends Omit<ButtonProps, "leftIcon" | "rightIcon" | "iconSpacing" | "isUppercase">,
   VariantProps<typeof IconButtonAnatomy.iconButton>, ComponentWithAnatomy<typeof IconButtonAnatomy> {
   icon?: React.ReactElement<any, string | React.JSXElementConstructor<any>>
}

export const IconButton = React.forwardRef<HTMLButtonElement, IconButtonProps>((props, ref) => {

   const {
      children,
      className,
      icon,
      size,
      iconButtonClassName,
      ...rest
   } = props

   return (
      <>
         <Button
            className={cn(
               IconButtonAnatomy.iconButton({ size }),
               iconButtonClassName,
               className,
            )}
            {...rest}
            ref={ref}
         >
            {icon}
         </Button>
      </>
   )

})

IconButton.displayName = "IconButton"

import { cn } from "@rahimstack/tailwind-utils"
import React from "react"
import { IconButton, IconButtonProps } from "../icon-button"

export interface CloseButtonProps extends IconButtonProps {
   icon?: React.ReactElement<any, string | React.JSXElementConstructor<any>>
}

export const CloseButton = React.forwardRef<HTMLButtonElement, CloseButtonProps>((props, ref) => {

   const {
      children,
      className,
      icon = undefined,
      size = "sm",
      ...rest
   } = props

   return (
      <>
         <IconButton
            type="button"
            intent="gray-outline"
            size={size}
            className={cn(
               "rounded-full text-2xl",
               className,
            )}
            icon={<span>
               <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16" width="16" height="16" fill="currentColor">
                  <path d="M3.72 3.72a.75.75 0 0 1 1.06 0L8 6.94l3.22-3.22a.749.749 0 0 1 1.275.326.749.749 0 0 1-.215.734L9.06 8l3.22 3.22a.749.749 0 0 1-.326 1.275.749.749 0 0 1-.734-.215L8 9.06l-3.22 3.22a.751.751 0 0 1-1.042-.018.751.751 0 0 1-.018-1.042L6.94 8 3.72 4.78a.75.75 0 0 1 0-1.06Z"></path>
               </svg>
            </span>}
            {...rest}
            ref={ref}
         />
      </>
   )

})

CloseButton.displayName = "CloseButton"

import React from "react"

interface ShowOnlyProps {
