"use client"

import { cn } from "@rahimstack/tailwind-utils"
import * as combobox from "@zag-js/combobox"
import { normalizeProps, useMachine } from "@zag-js/react"
import { cva } from "class-variance-authority"
import _find from "lodash/find"
import _isEmpty from "lodash/isEmpty"
import React, { useEffect, useId, useMemo, useState } from "react"
import { BasicField, BasicFieldOptions, extractBasicFieldProps } from "../basic-field"
import { ComponentWithAnatomy, defineStyleAnatomy } from "../core"
import { InputAddon, InputAnatomy, inputContainerStyle, InputIcon, InputStyling } from "../input"


export const ComboboxAnatomy = defineStyleAnatomy({
   menuContainer: cva([
      "UI-Combobox__menuContainer",
      "absolute z-20 -bottom-0.5",
      "left-0 translate-y-full max-h-56 w-full overflow-auto rounded-md p-2 text-base shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none sm:text-sm",
      // Light
      "bg-white",
      // Dark
      "dark:bg-gray-800",
   ]),
   menuItem: cva([
      "UI-Combobox__menuItem",
      "relative cursor-pointer py-2 pl-3 pr-9 rounded-md data-highlighted:bg-gray-100 dark:data-highlighted:bg-gray-700 text-base",
   ]),
   menuNoOptionText: cva([
      "UI-Combobox__menuNoOptionText",
      "text-base text-center py-1 text-gray-500 dark:text-gray-700",
   ]),
})

export interface ComboboxProps extends Omit<React.ComponentPropsWithRef<"input">, "onChange" | "size" | "defaultChecked">,
   BasicFieldOptions,
   InputStyling,
   ComponentWithAnatomy<typeof ComboboxAnatomy> {
   options: { value: string, label?: string }[]
   /* Filter the specified options as the user is typing */
   withFiltering?: boolean
   /* Get the value on of the input as the user is typing */
   onInputChange?: (value: string) => void
   /* Get the selected value */
   onChange?: (value: string | undefined) => void
   placeholder?: string
   /* Message to display when there is no option */
   noOptionsMessage?: string
   /* Allow the user to enter custom values that are not specified in the options */
   allowCustomValue?: boolean
   /* Either value or label */
   defaultValue?: string
   valueInputRef?: React.Ref<HTMLInputElement>
   /**
    * We can either return the value or label of the options.
    * Returning the label is useful when you allow users to put custom values or if the selection doesn't depend on IDs but the visible text.
    */
   returnValueOrLabel?: "value" | "label"
}

export const Combobox = React.forwardRef<HTMLInputElement, ComboboxProps>((props, ref) => {

   const [{
      size,
      intent,
      leftIcon,
      leftAddon,
      rightIcon,
      rightAddon,
      children,
      className,
      options,
      withFiltering = true,
      placeholder,
      noOptionsMessage,
      allowCustomValue = false,
      onInputChange,
      valueInputRef,
      defaultValue,
      onChange,
      value, // /!\ Unused
      returnValueOrLabel = "value",
      menuContainerClassName,
      menuItemClassName,
      menuNoOptionTextClassName,
      ...rest
   }, { id, ...basicFieldProps }] = extractBasicFieldProps<ComboboxProps>(props, useId())

   const [data, setData] = useState(options)

   const [selectedValue, setSelectedValue] = useState<string | undefined>(undefined)

   const [state, send] = useMachine(
      combobox.machine({
         id: useId(),
         allowCustomValue: allowCustomValue,
         inputBehavior: "autohighlight",
         openOnClick: true,
         loop: true,
         blurOnSelect: true,
         placeholder: placeholder,
         onOpen() {
            setData(options)
         },
         onSelect: (details) => {
            if (returnValueOrLabel === "value") {
               setSelectedValue(details.value)
               onChange && onChange(details.value)

            } else if (returnValueOrLabel === "label") {
               setSelectedValue(details.label)
               onChange && onChange(details.label)
            }
         },
         onInputChange({ value }) {
            if (withFiltering) {
               const filtered = options.filter((item) => {
                     if (item.label) {
                        return item.label.toLowerCase().includes(value.toLowerCase())
                     } else {
                        return item.value.toLowerCase().includes(value.toLowerCase())
                     }
                  },
               )
               setData(filtered.length > 0 ? filtered : data)
            }
            onInputChange && onInputChange(value)
         },
      }),
   )

   const api = combobox.connect(state, send, normalizeProps)

   useEffect(() => {
      if (returnValueOrLabel === "value") {
         if (defaultValue) {
            setSelectedValue(defaultValue)
            api.setInputValue(_find(options, ["value", defaultValue])?.label ?? "")
            api.setValue(_find(options, ["value", defaultValue])?.value ?? "")
         }
      }
      if (returnValueOrLabel === "label") {
         if (defaultValue) {
            setSelectedValue(_find(options, ["label", defaultValue])?.value ?? defaultValue)
            api.setInputValue(_find(options, ["label", defaultValue])?.label ?? defaultValue)
            api.setValue(_find(options, ["label", defaultValue])?.value ?? defaultValue)
         }
      }
   }, [defaultValue])

   const list = useMemo(() => {
      return withFiltering ? data : options
   }, [options, withFiltering, data])

   return (
      <>
         <BasicField
            {...basicFieldProps}
            id={api.inputProps.id}
            ref={ref}
         >
            <input type="text" hidden value={selectedValue ?? ""} onChange={() => {}} ref={valueInputRef} />

            <div {...api.rootProps}>
               <div {...api.controlProps} className={cn(inputContainerStyle())}>

                  <InputAddon addon={leftAddon} rightIcon={rightIcon} leftIcon={leftIcon} size={size} side={"left"} />
                  <InputIcon icon={leftIcon} size={size} side={"left"} props={api.triggerProps} />

                  <input
                     className={cn(
                        "unstyled",
                        InputAnatomy.input({
                           size,
                           intent,
                           hasError: !!basicFieldProps.error,
                           untouchable: !!basicFieldProps.isDisabled,
                           hasRightAddon: !!rightAddon,
                           hasRightIcon: !!rightIcon,
                           hasLeftAddon: !!leftAddon,
                           hasLeftIcon: !!leftIcon,
                        }),
                     )}
                     onBlur={() => {
                        /* If we do not allow custom values and the user blurs the input, we reset the input */
                        if (!allowCustomValue) {
                           if (options.length === 0 && !api.selectedValue || (api.selectedValue && api.selectedValue.length === 0)) {
                              api.setInputValue("")
                           }

                           if (
                              options.length > 0 &&
                              (!_isEmpty(_find(options, ["value", api.selectedValue])?.label)
                                 || !_isEmpty(_find(options, ["value", api.selectedValue])?.value)
                              )
                           ) {
                              api.selectedValue && api.setValue(api.selectedValue)
                           }
                        }
                     }}
                     {...rest}
                     ref={ref}
                     {...api.inputProps}
                  />

                  <InputAddon addon={rightAddon} rightIcon={rightIcon} leftIcon={leftAddon} size={size} side={"right"} />
                  <InputIcon icon={rightIcon} size={size} side={"right"} props={api.triggerProps} />

               </div>
            </div>

            {/* Menu */}
            <div {...api.positionerProps} className="z-10">
               {(!!noOptionsMessage || list.length > 0) && (
                  <ul
                     className={cn(ComboboxAnatomy.menuContainer(), menuContainerClassName)}
                     {...api.contentProps}
                  >
                     {(list.length === 0 && !!noOptionsMessage) &&
                         <div className={cn(ComboboxAnatomy.menuNoOptionText(), menuNoOptionTextClassName)}>{noOptionsMessage}</div>}
                     {list.map((item, index) => (
                        <li
                           className={cn(
                              ComboboxAnatomy.menuItem(),
                              menuItemClassName,
                           )}
                           key={`${item.value}:${index}`}
                           {...api.getOptionProps({
                              label: item.label ?? item.value,
                              value: item.value,
                              index,
                              disabled: basicFieldProps.isDisabled,
                           })}
                        >
                           {item.label}
                        </li>
                     ))}
                  </ul>
               )}
            </div>
         </BasicField>
      </>
   )

})

Combobox.displayName = "Combobox"

import { cn } from "@rahimstack/tailwind-utils"
import { cva, VariantProps } from "class-variance-authority"
import React from "react"
import { defineStyleAnatomy } from "../core"

export const InputAnatomy = defineStyleAnatomy({
   input: cva([
      "UI-Input__input",
      "w-full rounded-md",
      // Light
      "bg-white border-gray-300 placeholder-gray-400",
      "disabled:bg-gray-50 disabled:border-gray-200 disabled:text-gray-500",
      // Dark
      "dark:bg-gray-900 dark:border-gray-700 dark:placeholder-gray-600",
      "dark:disabled:bg-gray-800 dark:disabled:border-gray-800",

      "disabled:shadow-none disabled:pointer-events-none",
      "focus:border-brand-500 focus:ring-1 focus:ring-brand-500",
      "outline-none focus:outline-none",
      "transition duration-150",
      "shadow-sm",
   ], {
      variants: {
         size: {
            sm: "px-2 py-1.5 text-sm",
            md: "",
            lg: "px-4 py-3 text-md",
         },
         intent: {
            basic: "hover:border-gray-300",
            filled: "bg-gray-100 border-gray-100 focus:bg-white",
         },
         hasError: {
            false: null,
            true: "border-red-500 hover:border-red-200 dark:border-red-500",
         },
         untouchable: {
            false: null,
            true: "bg-gray-50 border-gray-200 text-gray-500 shadow-none pointer-events-none dark:bg-gray-800 dark:border-gray-800",
         },
         hasLeftAddon: { true: null, false: null },
         hasRightAddon: { true: null, false: null },
         hasLeftIcon: { true: null, false: null },
         hasRightIcon: { true: null, false: null },
      },
      compoundVariants: [
         { hasLeftAddon: true, className: "border-l-transparent hover:border-l-transparent rounded-l-none" },
         /**/
         { hasRightAddon: true, className: "border-r-transparent hover:border-r-transparent rounded-r-none" },
         /**/
         { hasLeftAddon: false, hasLeftIcon: true, size: "sm", className: "pl-10" },
         { hasLeftAddon: false, hasLeftIcon: true, size: "md", className: "pl-10" },
         { hasLeftAddon: false, hasLeftIcon: true, size: "lg", className: "pl-12" },
         /**/
         { hasRightAddon: false, hasRightIcon: true, size: "sm", className: "pr-10" },
         { hasRightAddon: false, hasRightIcon: true, size: "md", className: "pr-10" },
         { hasRightAddon: false, hasRightIcon: true, size: "lg", className: "pr-12" },
      ],
      defaultVariants: {
         size: "md",
         intent: "basic",
         hasError: false,
         untouchable: false,
         hasLeftIcon: false,
         hasRightIcon: false,
         hasLeftAddon: false,
         hasRightAddon: false,
      },
   }),
})


export interface InputStyling extends Omit<VariantProps<typeof InputAnatomy.input>, "untouchable" | "hasError" | "hasLeftAddon" | "hasRightAddon" | "hasLeftIcon" | "hasRightIcon"> {
   leftAddon?: string
   leftIcon?: React.ReactNode
   rightAddon?: string
   rightIcon?: React.ReactNode
}

/**
 * @description "flex relative"
 */
export const inputContainerStyle = () => cn("UI-Input__inputContainer flex relative")


// -------------------------------------------------------------------------------------------------------------------------------- //

export const InputAddonsAnatomy = defineStyleAnatomy({
   icon: cva([
      "UI-Input__addons--icon pointer-events-none absolute inset-y-0 grid place-content-center text-gray-500 z-[1]",
      "dark:text-gray-300",
   ], {
      variants: {
         size: { sm: "w-10 text-md", md: "w-12 text-lg", lg: "w-14 text-2xl" },
         isLeftIcon: { true: "left-0", false: null },
         isRightIcon: { true: "right-0", false: null },
      },
      defaultVariants: {
         size: "md",
         isLeftIcon: false, isRightIcon: false,
      },
   }),
   addon: cva([
      "UI-Input__addons--addon bg-gray-50 inline-flex items-center flex-none px-3 border border-gray-300 text-gray-800 shadow-sm text-sm sm:text-md",
      "dark:bg-gray-700 dark:border-gray-700 dark:text-gray-300",
   ], {
      variants: {
         size: { sm: "text-sm", md: "text-md", lg: "text-lg" },
         isLeftAddon: { true: "rounded-l-md", false: null },
         isRightAddon: { true: "rounded-r-md", false: null },
         hasLeftIcon: { true: null, false: null },
         hasRightIcon: { true: null, false: null },
      },
      compoundVariants: [
         { size: "sm", hasLeftIcon: true, isLeftAddon: true, className: "pl-10" },
         { size: "sm", hasRightIcon: true, isRightAddon: true, className: "pr-10" },
         { size: "md", hasLeftIcon: true, isLeftAddon: true, className: "pl-10" },
         { size: "md", hasRightIcon: true, isRightAddon: true, className: "pr-10" },
         { size: "lg", hasLeftIcon: true, isLeftAddon: true, className: "pl-12" },
         { size: "lg", hasRightIcon: true, isRightAddon: true, className: "pr-12" },
      ],
      defaultVariants: {
         size: "md",
         isLeftAddon: false, isRightAddon: false, hasLeftIcon: false, hasRightIcon: false,
      },
   }),
})


export const InputIcon = ({ icon, size = "md", side, props }: {
      icon: InputStyling["rightIcon"] | undefined,
      size: InputStyling["size"],
      side: "right" | "left",
      props?: Omit<React.ComponentPropsWithoutRef<"span">, "className">,
   },
) => {

   if (!!icon) return <span
      className={cn(InputAddonsAnatomy.icon({ isRightIcon: side === "right", isLeftIcon: side === "left", size }))}
      {...props}
   >
      {icon}
   </span>

   return null
}

export const InputAddon = ({ addon, leftIcon, rightIcon, size = "md", side, props }: {
      addon: InputStyling["rightAddon"] | InputStyling["leftAddon"] | undefined,
      rightIcon: InputStyling["leftIcon"] | undefined,
      leftIcon: InputStyling["rightIcon"] | undefined,
      size: InputStyling["size"],
      side: "right" | "left",
      props?: Omit<React.ComponentPropsWithoutRef<"span">, "className">,
   },
) => {

   if (!!addon) return (
      <span
         className={cn(InputAddonsAnatomy.addon({
            isRightAddon: side === "right",
            isLeftAddon: side === "left",
            hasRightIcon: !!rightIcon,
            hasLeftIcon: !!leftIcon,
            size,
         }))}
         {...props}
      >
         {addon}
      </span>
   )

   return null

}

"use client"

import { LoaderOptions } from "@googlemaps/js-api-loader"
import _isEmpty from "lodash/isEmpty"
import React, { useId } from "react"
import { Combobox, ComboboxProps, extractBasicFieldProps, useUILocaleConfig } from ".."
import locales from "./locales.json"
import { GoogleMapsAutocompletionRequest, useGoogleMapsAutocomplete } from "./use-address-autocomplete"

export interface AddressInputProps extends Omit<ComboboxProps, "options" | "onInputChange" | "onChange"> {
   autocompletionRequest?: GoogleMapsAutocompletionRequest
   apiOptions?: Partial<LoaderOptions>
   allowedCountries?: string | string[] | null
   onChange?: (value: string | undefined) => void
   noOptionsMessage?: string
   placeholder?: string
   apiKey: string // Optionally, you could remove this parameter and get the key from environment variables
}

export const AddressInput = React.forwardRef<HTMLInputElement, AddressInputProps>((props, ref) => {

   const { locale: lng } = useUILocaleConfig()

   const [{
      children,
      className,
      autocompletionRequest,
      apiOptions,
      defaultValue,
      allowedCountries = null,
      onChange,
      apiKey,
      placeholder = locales["placeholder"][lng],
      noOptionsMessage = locales["no-address-found"][lng],
      ...rest
   }, basicFieldProps] = extractBasicFieldProps<AddressInputProps>(props, useId())

   const { suggestions, fetchSuggestions } = useGoogleMapsAutocomplete({
      apiKey: apiKey,
      minLengthAutocomplete: 0,
      withSessionToken: false,
      debounce: 300,
      autocompletionRequest: {
         componentRestrictions: { country: allowedCountries },
      },
   })

   return (
      <>
         <Combobox
            returnValueOrLabel="label" // We only return the address' text format
            allowCustomValue={false}
            withFiltering={false} // We deactivate filtering because the options are automatically filtered by the API
            options={_isEmpty(suggestions) && defaultValue ? [{ value: defaultValue, label: defaultValue }] : suggestions}
            onInputChange={fetchSuggestions}
            defaultValue={defaultValue}
            onChange={onChange}
            placeholder={placeholder}
            noOptionsMessage={noOptionsMessage}
            {...basicFieldProps}
            {...rest}
            ref={ref}
         />
      </>
   )

})

"use client"

import { Loader, LoaderOptions } from "@googlemaps/js-api-loader"
import { useCallback, useEffect, useState } from "react"
import { useDebouncedCallback } from "use-debounce"

export type GoogleMapsAutocompletionRequest = Omit<google.maps.places.AutocompletionRequest, "input">

export interface GoogleMapsAutocompleteProps {
   apiKey: string,
   minLengthAutocomplete?: number,
   withSessionToken?: boolean
   debounce?: number
   autocompletionRequest?: GoogleMapsAutocompletionRequest
   loaderApiOptions?: Partial<LoaderOptions>
}

export const useGoogleMapsAutocomplete = ({
   apiKey,
   minLengthAutocomplete = 0,
   withSessionToken = false,
   debounce = 300,
   autocompletionRequest,
   loaderApiOptions = {},
}:
   GoogleMapsAutocompleteProps) => {

   const [autocompleteService, setAutocompleteService] = useState<google.maps.places.AutocompleteService | undefined>(undefined)
   const [sessionToken, setSessionToken] = useState<google.maps.places.AutocompleteSessionToken | undefined>(undefined)

   const initializeService = useCallback(() => {
      if (!window.google) throw new Error("[AddressInput]: Google script not loaded")
      if (!window.google.maps) throw new Error("[AddressInput]: Google maps script not loaded")
      if (!window.google.maps.places) throw new Error("[AddressInput]: Google maps places script not loaded")

      setAutocompleteService(new window.google.maps.places.AutocompleteService())
      setSessionToken(new google.maps.places.AutocompleteSessionToken())
   }, [window])

   /**
    * Initialize
    */
   useEffect(() => {
      const init = async () => {
         try {
            if (!window.google || !window.google.maps || !window.google.maps.places) {
               await new Loader({ apiKey: apiKey, ...{ libraries: ["places"], ...loaderApiOptions } }).load()
            }
            initializeService()
         }
         catch (error) {
            console.log(error)
         }
      }

      if (apiKey) init()
      else initializeService()
   }, [])


   /**
    * Fetch suggestions
    */
   const [suggestions, setSuggestions] = useState<{ label: string, value: string }[]>([])

   const fetchSuggestions = useDebouncedCallback((value: string): void => {
      if (!autocompleteService) return setSuggestions([])
      if (value.length < minLengthAutocomplete) return setSuggestions([])

      const autocompletionReq: GoogleMapsAutocompletionRequest = { ...autocompletionRequest }

      autocompleteService.getPlacePredictions(
         requestBuilder(
            autocompletionReq,
            value,
            withSessionToken && sessionToken,
         ), (suggestions) => {
            setSuggestions((suggestions || []).map(suggestion => ({ label: suggestion.description, value: suggestion.place_id })))
         },
      )
   }, debounce)

   return {
      suggestions,
      fetchSuggestions,
   }

}

const requestBuilder = (
   autocompletionRequest: GoogleMapsAutocompletionRequest,
   input: string,
   sessionToken?: google.maps.places.AutocompleteSessionToken,
): google.maps.places.AutocompletionRequest => {
   const { location, ...rest } = autocompletionRequest

   const res: google.maps.places.AutocompletionRequest = {
      input,
      ...rest,
   }

   if (sessionToken) {
      res.sessionToken = sessionToken
   }

   if (location) {
      res.location = new google.maps.LatLng(location)
   }

   return res
}

